mapper.update:
begin
for x in 1 .. 10 loop
update stock_list set product_amount=product_amount - 1 
where product_name in (
select 
trim(regexp_substr(replace(menu_recipe,'/','/ '),'[^/]+',1,x))
from MENU_LIST
where MENU_CODE in (select menu_code from sales_list where menu_code = #{menu_code})
);
end loop;
end;
